class ShowQr{constructor(){this.buttons=document.querySelectorAll(".js-show-qr"),this.qr,this.setup()}setup=()=>{this.buttons.forEach(s=>{this.qr=s.nextElementSibling,s.addEventListener("click",this.showQr.bind(null,this.qr,s))})};showQr=(s,t)=>{s.classList.contains("show")?(s.classList.remove("show"),t.querySelector("span").innerText="Показать"):(s.classList.add("show"),t.querySelector("span").innerText="Скрыть")}}