class Tabs{constructor(t){this.$tabs=document.querySelectorAll(".js-tabs"),this.options=t,this.$tabContents,this.$tabBtns,this.setup()}setup=()=>{this.$tabs.forEach(t=>{[...t.children].forEach(t=>{t.classList.contains("tab-links")?this.$tabBtns=t.querySelectorAll(this.options.tabBtns):this.$tabContents=t.children}),this.$tabBtns.forEach(t=>{t.addEventListener("click",this.clickHandler.bind(null,this.$tabBtns,this.$tabContents))})})};clickHandler=(t,s,e)=>{e.preventDefault();const a=e.currentTarget;e=a.getAttribute("href").slice(1);const i=document.getElementById(e);t.forEach(t=>{t.classList.remove("active")}),a.classList.add("active"),[...s].forEach(t=>{t.classList.remove("active")}),i.classList.add("active")}}