class Input{constructor(e){this.$elements=document.querySelectorAll(e),this.checkChangedInputs(),this.setup()}setup=()=>{this.$elements.forEach(e=>{e.addEventListener("change",this.onChangeHandler)})};checkChangedInputs=()=>{this.$elements.forEach(e=>{("TEXTAREA"!==e.tagName||null==e.getAttribute("placeholder"))&&""===e.value?e.classList.remove("changed"):e.classList.add("changed")})};onChangeHandler=e=>{const t=e.currentTarget;""===t.value?t.classList.remove("changed"):t.classList.add("changed")};onFocusHandler=e=>{e=e.currentTarget;const t=e.nextElementSibling;e.parentNode.nextElementSibling?t.classList.add("hide"):(t.style.transition="all 0.3s ease",t.classList.remove("hide"))}}